package com.example.root.jobify;

import android.content.Context;
import android.util.Log;
import android.widget.Toast;

import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Date;

/**
 * Created by dani on 25/09/16.
 */
public class Globals {

    public static final int DATE_LONG_STYLE = 0;
    public static final int DATE_SHORT_STYLE = 1;

    public static Date parseDate(String dateString, int style) {
        String format = (style == DATE_LONG_STYLE) ? "yyyy-MM-dd'T'hh:mm:ss.SSS'Z'" : "yyyy-MM-dd";
        SimpleDateFormat dateFormat = new SimpleDateFormat(format);
        Date convertedDate = null;
        try {
            convertedDate = dateFormat.parse(dateString);
        } catch (ParseException e) {
            Log.d("Date parsing", "The date didn have the format expected.");
        }
        return convertedDate;
    }

    public static boolean areValidCredentials(final String email, final String password, Context context){
        if (email.length()>0 && password.length()>0){
            if(email.contains("@") && email.contains(".")){
                return true;
            } else {
                Toast.makeText(context, R.string.invalid_email_string,Toast.LENGTH_LONG).show();
            }
        } else {
            Toast.makeText(context,context.getString(R.string.credentials_auth_not_fullfilled_string),Toast.LENGTH_LONG).show();
        }
        return false;
    }


    public static String defaultBase64Image="";

}
